import { useState } from 'react'; 

export default function board(){
    const[currentPlayer, setCurrentPlayer] = useState(1);
    const [winner, setWinner] = useState(null);

    const[board, setBoard] = useState(() => Array.from({length: 15}, () => Array(15).fill(0)))
    function restartGame(){
        setSquares(Array.from({ length: 15 }, () => Array(15).fill(0)));
        setCurrentPlayer(1);
    }
    function resetScores(){
        let i; 
    }
    function handleClick(row, col){
        if(board[row][col] !== 0 || winner ){
            return;
        }
        const newBoard = board.map((r, i) =>
            r.map((cell, j) => (i === row && j === col ?  currentPlayer : cell))
    );
    setBoard(newBoard); 
    if(checkWins(newBoard, row, col)){
        setWinner(currentPlayer);
        console.log("Player", currentPlayer, "wins!");
    }else{
        setCurrentPlayer(currentPlayer === 1 ? 2 : 1); 
    }
    }
    return (
        <div className="board-container">
            <div>
                {board.map((row, i) => (
                    <div key={i} className="row">
                        {row.map((cell, j) => (
                            <Square 
                             key={`${i}-${j}`}
                             value={cell}
                             onSquareClick={() => handleClick(i, j)}
                            />
                        ))}
                </div>
                ))}
              <div className="restart">
                  <button className="restart-button" onClick={restartGame}>
                      Restart Game
                   </button>
                  <button className="reset-button" onClick={resetScore}>
                      Reset Scores
                 </button>
             </div>
            </div>
        </div>
    )
}
function Square({value, onSquareClick}){
    let className = "square"; 
    if(value === "X") className += " x-color";
    else if (value === "O") className += " o-color"; 
    return(
        <button className={className} onClick={onSquareClick}>
             {value}
        </button>
    )
};

function checkWins(board, row, col){
    const player = board[row][col];
    const directions = [[0, 1], [1, 0], [1, 1], [1, -1]];
    const isValid = (r,c) => r >= 0 && r < 15 && c>=0 && c < 15;
    for(let [dr, dc] of directions){
        let count = 1; 

        //forwards
        let r = row + dr, c = col + dc; 
        while(isValid(r,c)  && board[r][c] === player){
            count++;
            r+= dr;
            c+= dc; 
        }
        //backwards
        r = row - dr; c = col - dc; 
        while(isValid(r,c) && board[r][c] === player){
            count++; 
            r -= dr; 
            c -= dc; 
        }
        if(count >= 5){
            return true; 
        }
    }
    return false; 


}
