import { useState } from 'react'; 

export default function board(){
    const[currentPlayer, setCurrentPlayer] = useState("X");
    const[board, setBoard] = useState(() => Array.from({length: 15}, () => Array(15).fill(7)))
    function restartGame(){
        setSquares(Array.from({ length: 15 }, () => Array(15).fill(7)));
        setXisNext(true);
    }
    function handleClick(row, col){
        if(board[row][col] != 0){
            return;
        }
        const newBoard = board.map((r, i) => r.map((cell, j) => {
            if(i === row && j === col){
                return currentPlayer; 
            }
            return cell; 
        })
    ); 
    setBoard(newBoard); 
    }
    return (
        <div className="board-container">
            <div>
                {board.map((row, i) => (
                    <div key={i} className="row">
                        {row.map((cell, j) => (
                            <Square 
                             key={`${i}-${j}`}
                             value={cell}
                             onClick={() => handleClick(i, j)}
                            />
                        ))}
                </div>
                ))}
            </div>
        </div>
    )
}
function Square({value, onSquareClick}){
    let className = "square"; 
    if(value === "X") className += " x-color";
    else if (value === "O") className += " o-color"; 
    return(
        <button className={className} onClick={onSquareClick}>
             {value}
        </button>
    )
}
